mapper.select:
select *
from robdb.module m
where #{path} = m.request or #{path} regexp concat(m.request , '/[a-z]+$') or #{path} = m.request;

select parentid
from module
where moduleid = #{moduleId}

select modulestatus
from module
where moduleid = #{moduleId}

mapper.update:
update module
set modulestatus = #{moduleStatus}
where moduleid = #{moduleId} or parentid = #{moduleId}
