mapper.update:
update article
articlestatus = #{questionstatus}, title = #{questiontitle}, content = #{questioncontent}, cost = #{cost}, lastmodifytime = now()
where articleid = #{questionid}

mapper.select:
select count(tagid)
from tag
where ownerid = #{userid}

select count(tagid)
from article_has_tag
where articleid=#{questionid}

seletct articleid questionid,tagid tagid,tagname tagname
from tag, article_has_tag
where articleid = #{questionid}
